import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from '../../scss/mypage/MyinfoEdit.module.scss';

function MyinfoEdit() {
    const navigate = useNavigate();
    const [userInfo, setUserInfo] = useState({
        customer_name: '',
        email: '',
        contact_number: '',
        optional_agree: 0,
        birth_date: '',
    });
    const [passwords, setPasswords] = useState({
        currentPassword: '',
        newPassword: '',
        confirmPassword: '',
    });
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const sessionToken = sessionStorage.getItem('sessionToken');
        const email = sessionStorage.getItem('email');

        if (!sessionToken || !email) {
            navigate('/signIn');
        } else {
            // Fetch user data
            axios
                .post(
                    'http://localhost:5001/myPage', // customers DB 정보 가져오기
                    { email },
                    { headers: { Authorization: sessionToken } }
                )
                .then(response => {
                    setUserInfo(response.data);
                })
                .catch(() => navigate('/signIn'));

            // Fetch password data from auth DB
            axios
                .post(
                    'http://localhost:5001/auth', // auth DB 정보 가져오기
                    { email },
                    { headers: { Authorization: sessionToken } }
                )
                .then(response => {
                    setPasswords({ ...passwords, currentPassword: response.data.password });
                })
                .catch(() => navigate('/signIn'));
        }
    }, [navigate]);

    const handlePasswordChange = () => {
        // 비밀번호 변경 로직
        if (passwords.newPassword === passwords.confirmPassword) {
            axios
                .put('http://localhost:5001/auth/password', { email: userInfo.email, newPassword: passwords.newPassword }, { headers: { Authorization: sessionStorage.getItem('sessionToken') } })
                .then(() => {
                    alert('비밀번호가 변경되었습니다.');
                    setIsModalOpen(false);
                })
                .catch(() => alert('비밀번호 변경에 실패했습니다.'));
        } else {
            alert('새 비밀번호가 일치하지 않습니다.');
        }
    };

    return (
        <main>
            {/* 상단 안내 메시지 */}
            <p className={styles.infomessage}>
                회원가입이 완료되었습니다.
                <br />
                추가 선택 정보를 기입하시려면 아래 배송지 및 생일 등 선택 정보를 업데이트 하여 주십시오.
            </p>

            <div className={styles.block}>
                <h3>개인정보 관리</h3>
                <div className={styles.requiredinfo}>*는 필수 입력 정보입니다</div>

                <fieldset className={styles.infoinput}>
                    <div className={styles.inputgroup}>
                        <label>*성, 이름</label>
                        <input type="text" value={userInfo.customer_name} onChange={e => setUserInfo({ ...userInfo, customer_name: e.target.value })} className={styles.inputname} />

                        <label>*이메일 주소</label>
                        <input type="text" value={userInfo.email} readOnly className={styles.inputemail} />
                    </div>

                    {/* 비밀번호 변경 모달 */}
                    {isModalOpen && (
                        <div className={styles.modal}>
                            <div className={styles.modalcontent}>
                                <div className={styles.modalheader}>
                                    <span>비밀번호를 변경해주세요</span>
                                    <button className={styles.modalclose} onClick={() => setIsModalOpen(false)}>
                                        &times;
                                    </button>
                                </div>
                                <div className={styles.modalbody}>
                                    <input type="password" placeholder="현재 비밀번호" value={passwords.currentPassword} readOnly className={styles.modalinput} />
                                    <input type="password" placeholder="새로운 비밀번호" value={passwords.newPassword} onChange={e => setPasswords({ ...passwords, newPassword: e.target.value })} className={styles.modalinput} />
                                    <input type="password" placeholder="비밀번호 재확인" value={passwords.confirmPassword} onChange={e => setPasswords({ ...passwords, confirmPassword: e.target.value })} className={styles.modalinput} />
                                </div>
                                <div className={styles.modalfooter}>
                                    <button className={styles.modalcancel} onClick={() => setIsModalOpen(false)}>
                                        취소
                                    </button>
                                    <button className={styles.modalconfirm} onClick={handlePasswordChange}>
                                        확인
                                    </button>
                                </div>
                            </div>
                        </div>
                    )}

                    <button className={styles.btnopenmodal} onClick={() => setIsModalOpen(true)}>
                        비밀번호 재설정
                    </button>
                </fieldset>
            </div>

            <div className={styles.block}>
                {/* 휴대전화 번호 */}
                <label>*휴대전화 번호</label>
                <input type="text" value={userInfo.contact_number} onChange={e => setUserInfo({ ...userInfo, contact_number: e.target.value })} className={styles.phoneinput} />
            </div>

            <div className={styles.block}>
                {/* 뉴스레터 정보 */}
                <h4>조 말론 런던 뉴스레터</h4>
                <div className={styles.checkboxgroup}>
                    <label>뉴스레터 수신여부</label>
                    <input type="checkbox" checked={userInfo.optional_agree === 1} onChange={() => setUserInfo({ ...userInfo, optional_agree: userInfo.optional_agree === 1 ? 0 : 1 })} />
                </div>
            </div>

            <div className={styles.block}>
                {/* 생일 */}
                <h4>생일</h4>
