import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { DateRangePicker } from 'react-date-range';
import 'react-date-range/dist/styles.css'; // 기본 스타일
import 'react-date-range/dist/theme/default.css'; // 테마 스타일
import axios from 'axios';
import styles from '../../../scss/member/MemberList.module.scss'; // SCSS 파일 임포트

function MemberList(props) {
    const [arr, setArr] = useState([]);
    const [filteredCustomers, setFilteredCustomers] = useState([]); // 필터링된 고객 데이터
    const [searchType, setSearchType] = useState(''); // 검색 기준
    const [searchValue, setSearchValue] = useState(''); // 검색 값

    const [dateRange, setDateRange] = useState([
        //date range picker
        {
            startDate: new Date(),
            endDate: new Date(),
            key: 'selection',
        },
    ]);

    useEffect(() => {
        axios
            .get('http://localhost:5001/admin/member')
            .then(res => {
                setArr(res.data);
                setFilteredCustomers(res.data); // 처음에는 전체 데이터 표시
            })
            .catch(err => {
                console.error('에러발생 : ', err);
            });
    }, []);

    // 검색 이벤트 핸들러
    const handleSearch = () => {
        const filtered = arr.filter(arr => {
            if (searchType === 'customer_name') {
                return arr.customer_name.includes(searchValue);
            } else if (searchType === 'contact_number') {
                return arr.contact_number.includes(searchValue);
            } else if (searchType === 'join_date') {
                const { startDate, endDate } = dateRange[0];
                filtered = filtered.filter(customer => {
                    const joinDate = new Date(customer.join_date);
                    return joinDate >= startDate && joinDate <= endDate;
                });
            }
            return true;
        });
        setFilteredCustomers(filtered);
    };

    // 초기화 버튼 이벤트 핸들러
    const resetSearch = () => {
        setSearchType(''); // 검색 기준 초기화
        setSearchValue(''); // 검색 값 초기화
        setDateRange([
            //date range picker
            {
                startDate: new Date(),
                endDate: new Date(),
                key: 'selection',
            },
        ]);
        setFilteredCustomers(arr); // 전체 고객 데이터로 복원
    };

    // 날짜 포맷팅 함수
    const formatDate = dateString => {
        if (!dateString) return '-';
        const date = new Date(dateString);
        return date.toISOString().split('T')[0];
    };

    return (
        <div className={styles.memberlist}>
            {/* 검색 바 */}
            <div className={styles.searchbar}>
                <select value={searchType} onChange={e => setSearchType(e.target.value)} style={{ padding: '5px', fontSize: '14px' }}>
                    <option value="">검색 기준 선택</option>
                    <option value="customer_name">이름으로 조회</option>
                    <option value="contact_number">연락처로 조회</option>
                    <option value="join_date">가입일로 조회</option>
                </select>

                {searchType === 'join_date' && <DateRangePicker ranges={dateRange} onChange={item => setDateRange([item.selection])} moveRangeOnFirstSelection={false} />}

                <input type="text" placeholder="검색어 입력" value={searchValue} onChange={e => setSearchValue(e.target.value)} style={{ padding: '5px', fontSize: '14px', width: '300px' }} />
                <button className={styles.searchbutton} onClick={handleSearch}>
                    검색
                </button>
                <button className={styles.resetbutton} onClick={resetSearch}>
                    초기화
                </button>
            </div>
            <table style={{ width: '100%', borderCollapse: 'collapse', alignItems: 'center', textAlign: 'center' }}>
                <thead>
                    <tr>
                        <th>
                            <input type="checkbox"></input>전체 선택
                        </th>
                        <th>고객번호</th>
                        <th>고객명</th>
                        <th>연락처</th>
                        <th>이메일주소</th>
                        <th>가입일</th>
                        <th>마지막접속일</th>
                        <th>상태</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredCustomers.map(mm => (
                        <tr key={mm.customer_id}>
                            <td>
                                <input type="checkbox"></input>
                            </td>
                            <td>
                                <Link to={`/admin/member/detail/${mm.customer_id}`}>{mm.customer_id}</Link>
                            </td>
                            <td>{mm.customer_name}</td>
                            <td>{mm.contact_number}</td>
                            <td>{mm.email_address}</td>
                            <td>{formatDate(mm.join_date)}</td>
                            <td>{formatDate(mm.last_login_date)}</td>
                            <td>{mm.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default MemberList;
