      fileName: _jsxFileName,
      lineNumber: 72,
      columnNumber: 7
    }, this), /*#__PURE__*/_jsxDEV("div", {
      className: "tterms",
      children: "6. \uC81C\uD55C\uC801 \uC774\uC6A9\uD5C8\uB77D"
    }, void 0, false, {
      fileName: _jsxFileName,
      lineNumber: 75,
      columnNumber: 7
    }, this), /*#__PURE__*/_jsxDEV("p", {
      children: "\uD68C\uC0AC\uB294 \uC774\uC6A9\uC790\uAC00 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0 \uC811\uC18D\uD558\uC5EC \uAC1C\uC778 \uC6A9\uB3C4\uB85C \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uB97C \uC774\uC6A9\uD560 \uC218 \uC788\uB294 \uC81C\uD55C\uC801\uC774\uACE0 \uCDE8\uC18C\uAC00\uB2A5\uD558\uBA70 \uBE44\uB3C5\uC810\uC801\uC778 \uC774\uC6A9\uAD8C\uD55C\uC744 \uC774\uC6A9\uC790\uC5D0\uAC8C \uD5C8\uC5EC\uD569\uB2C8\uB2E4. \uC774 \uC81C\uD55C\uC801 \uC774\uC6A9\uAD8C\uD55C\uC740 (a) \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB610\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC758 \uC77C\uBD80\uB97C \uD3EC\uD568\uD558\uC5EC \uD504\uB808\uC774\uBC0D\uD558\uAC70\uB098 \uD504\uB808\uC774\uBC0D \uAE30\uC220\uC744 \uD65C\uC6A9\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC, (b) \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB610\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uCEE8\uD150\uCE20\uC758 \uC77C\uBD80 \uBC0F/\uB610\uB294 \uC804\uBD80\uB97C \uC7AC\uCD9C\uD310, \uC7AC\uBC30\uD3EC, \uC804\uC1A1, \uD310\uB9E4\uD558\uAC70\uB098, \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB610\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB0B4 \uCEE8\uD150\uCE20\uC758 \uC77C\uBD80 \uBC0F/\uB610\uB294 \uC804\uBD80\uC5D0 \uC0AC\uC6A9\uAD8C\uC744 \uD5C8\uC5EC\uD558\uAC70\uB098, \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB4F1\uC744 \uB2E4\uC6B4\uB85C\uB4DC\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC(\uCE90\uC2F1\uC774\uB098 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uB97C \uC870\uD68C\uD558\uAE30 \uC704\uD558\uC5EC \uD544\uC694\uD55C \uACBD\uC6B0\uB97C \uC81C\uC678\uD569\uB2C8\uB2E4), (c) \uAC1C\uC778 \uC6A9\uB3C4\uB97C \uC81C\uC678\uD55C \uB2E4\uB978 \uBAA9\uC801\uC73C\uB85C \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uB098 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB0B4 \uCEE8\uD150\uCE20\uC758 \uC77C\uBD80 \uBC0F/\uB610\uB294 \uC804\uBD80\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC, (d) \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uB098 \uBE0C\uB79C\uB4DC \uC6F9\uCEE8\uD150\uCE20\uC758 \uC804\uBD80 \uB610\uB294 \uC77C\uBD80\uC5D0 \uAE30\uBC18\uD55C \uD30C\uC0DD\uBB3C\uC744 \uAC1C\uC791, \uC5ED\uBD84\uC11D, \uB610\uB294 \uC0DD\uC131\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC, (e) \uC774\uC6A9\uC790 \uB610\uB294 \uB2E4\uB978 \uC0C1\uB300\uBC29\uC758 \uC774\uC775\uC744 \uC704\uD558\uC5EC \uACC4\uC815 \uC815\uBCF4\uB97C \uC218\uC9D1\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC, (f) \uBA54\uD0C0 \uD0DC\uADF8 \uB610\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB0B4 \uCEE8\uD150\uCE20\uC758 \uC77C\uBD80 \uBC0F/\uB610\uB294 \uC804\uBD80\uB97C \uD65C\uC6A9\uD55C \u201C\uC228\uACA8\uC9C4 \uD14D\uC2A4\uD2B8\u201D\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC, \uB610\uB294 (g) \uC18C\uD504\uD2B8\uC6E8\uC5B4 \uB85C\uBD07, \uC2A4\uD30C\uC774\uB354, \uD06C\uB864\uB7EC \uB610\uB294 \uC774\uC640 \uC720\uC0AC\uD55C \uB370\uC774\uD130 \uC218\uC9D1 \uBC0F \uCD94\uCD9C \uB3C4\uAD6C\uB97C \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC\uB098 \uD68C\uC0AC\uC758 \uAE30\uBC18\uC2DC\uC124\uC5D0 \uBD88\uD569\uB9AC\uD55C \uBD80\uB2F4\uC774\uB098 \uBD80\uD558\uB97C \uC904 \uC218 \uC788\uB294 \uC5EC\uD0C0 \uD589\uB3D9\uC744 \uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC\uB97C \uD3EC\uD568\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC774\uC6A9\uC790\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC0C1 \uB610\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0 \uCCA8\uBD80\uB418\uAC70\uB098 \uADF8\uC5D0 \uD3EC\uD568\uB418\uB294 \uBAA8\uB4E0 \uAD8C\uB9AC\uC5D0 \uAD00\uD55C \uD1B5\uC9C0\uB97C \uBCC0\uACBD\uC5C6\uC774 \uBCF4\uC720\uD558\uC5EC\uC57C \uD569\uB2C8\uB2E4."
    }, void 0, false, {
      fileName: _jsxFileName,
      lineNumber: 77,
      columnNumber: 1
    }, this), /*#__PURE__*/_jsxDEV("p", {
      children: "\uD68C\uC0AC\uB294 \uB610\uD55C \uC774\uC6A9\uC790\uC5D0\uAC8C \uAC1C\uC778\uC801, \uBE44\uC0C1\uC5C5\uC801 \uC6A9\uB3C4\uC5D0 \uAD6D\uD55C\uD558\uC5EC \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC758 \uD648\uD398\uC774\uC9C0\uC5D0 \uD558\uC774\uD37C\uB9C1\uD06C\uB97C \uC124\uC815\uD560 \uC218 \uC788\uB294 \uC81C\uD55C\uC801\uC774\uACE0 \uCDE8\uC18C\uAC00\uB2A5\uD558\uBA70 \uBE44\uB3C5\uC810\uC801\uC778 \uC774\uC6A9\uAD8C\uD55C\uC744 \uD5C8\uC5EC\uD569\uB2C8\uB2E4. \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0 \uB9C1\uD06C\uB41C \uC6F9\uC0AC\uC774\uD2B8\uB294 (i) \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB0B4 \uCEE8\uD150\uCE20\uC758 \uC77C\uBD80 \uBC0F/\uB610\uB294 \uC804\uBD80\uC5D0 \uB9C1\uD06C\uB420 \uC218\uB294 \uC788\uC9C0\uB9CC \uBCF8 \uCEE8\uD150\uCE20\uB97C \uBCF5\uC81C\uD560 \uC218 \uC5C6\uACE0, (ii) \uD68C\uC0AC\uAC00 \uB9C1\uD06C\uB41C \uC0AC\uC774\uD2B8\uB098 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC758 \uC81C\uD488\uC774\uB098 \uC11C\uBE44\uC2A4\uB97C \uCD94\uCC9C\uD558\uB294 \uAC83\uC744 \uC758\uBBF8\uD558\uC9C0 \uC544\uB2C8\uD558\uBA70, (iii) \uB9C1\uD06C\uB41C \uC0AC\uC774\uD2B8\uC640 \uD68C\uC0AC\uC640\uC758 \uAD00\uACC4\uB97C \uD5C8\uC704\uB85C \uD45C\uC2DC\uD558\uAC70\uB098, (iv) \uD610\uC624\uC2A4\uB7FD\uACE0, \uC678\uC124\uC801\uC774\uBA70, \uBD88\uCF8C\uD558\uACE0, \uB17C\uC7C1\uC801\uC774\uAC70\uB098, \uBD88\uBC95\uC801\uC774\uAC70\uB098, \uBAA8\uB4E0 \uC5F0\uB839\uCE35\uC5D0\uAC8C \uBD80\uC801\uD569\uD55C \uAC83\uC73C\uB85C \uD574\uC11D\uB420 \uC218 \uC788\uB294 \uBCF8 \uCEE8\uD150\uCE20\uB97C \uD3EC\uD568\uD560 \uC218 \uC5C6\uC73C\uBA70, (v) \uD68C\uC0AC\uB098 \uD68C\uC0AC\uC758 \uC81C\uD488 \uB610\uB294 \uC11C\uBE44\uC2A4\uB97C \uD3C4\uD558\uD558\uAC70\uB098 \uD5C8\uC704\uB85C \uBB18\uC0AC\uD558\uAC70\uB098, \uB610\uB294 \uBD88\uCF8C\uD558\uAC70\uB098 \uD610\uC624\uC2A4\uB7EC\uC6B4 \uBC29\uC2DD\uC73C\uB85C \uBB18\uC0AC\uD560 \uC218 \uC5C6\uC73C\uBA70, \uD68C\uC0AC\uB97C \uBC14\uB78C\uC9C1\uD558\uC9C0 \uC54A\uC740 \uC81C\uD488, \uC11C\uBE44\uC2A4, \uC758\uACAC\uACFC \uC5F0\uAD00\uC2DC\uD0AC \uC218 \uC5C6\uACE0, (vi) \uD648\uD398\uC774\uC9C0 \uCCAB \uD654\uBA74 \uC774\uC678\uC758 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uC6F9\uD398\uC774\uC9C0\uB97C \uB9C1\uD06C\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. \uD68C\uC0AC\uB294 \uC7AC\uB7C9 \uD558\uC5D0 \uC774\uC6A9\uC790\uAC00 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0 \uC5F0\uACB0\uD55C \uBAA8\uB4E0 \uB9C1\uD06C\uB97C \uC0AD\uC81C\uD560 \uAC83\uC744 \uC694\uCCAD\uD560 \uC218 \uC788\uC73C\uBA70, \uC774\uC6A9\uC790\uB294 \uC774\uC640 \uAC19\uC740 \uC694\uCCAD\uC744 \uBC1B\uC740 \uC989\uC2DC \uD574\uB2F9 \uB9C1\uD06C\uB97C \uC0AD\uC81C\uD558\uACE0, \uB9C1\uD06C\uC124\uC815\uC744 \uC7AC\uAC1C\uD560 \uC218 \uC788\uB294 \uBCC4\uB3C4\uC758 \uBA85\uC2DC\uC801\uC778 \uC11C\uBA74 \uC2B9\uC778\uC744 \uD68C\uC0AC\uB85C\uBD80\uD130 \uB4DD\uD558\uC9C0 \uC54A\uB294 \uD55C \uC77C\uCCB4\uC758 \uB9C1\uD06C\uC124\uC815 \uD589\uC704\uB97C \uC911\uB2E8\uD558\uC5EC\uC57C \uD569\uB2C8\uB2E4. \uC774\uC6A9\uC790\uAC00 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8 \uB610\uB294 \uBCF8 \uCEE8\uD150\uCE20\uC758 \uC77C\uBD80 \uBC0F/\uB610\uB294 \uC804\uBD80\uB97C \uC2B9\uC778\uBC1B\uC9C0 \uC54A\uACE0 \uC0AC\uC6A9\uD560 \uACBD\uC6B0, \uD68C\uC0AC\uB294 \uBCF8 \uC57D\uAD00 \uC81C6\uC870\uC5D0 \uADDC\uC815\uB41C \uC774\uC6A9\uC790\uC758 \uC81C\uD55C\uC801 \uC774\uC6A9\uAD8C\uD55C\uC740 \uAD00\uB828 \uBC95\uB960\uC774\uB098 \uBCF8 \uC57D\uAD00\uC5D0 \uADDC\uC815\uB41C \uC5EC\uD0C0 \uAD6C\uC81C\uCC45\uC5D0 \uC601\uD5A5\uC744 \uBBF8\uCE58\uC9C0 \uC544\uB2C8\uD55C \uCC44 \uD574\uC9C0\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4."
    }, void 0, false, {
