    }, void 0, false, {
      fileName: _jsxFileName,
      lineNumber: 129,
      columnNumber: 1
    }, this), /*#__PURE__*/_jsxDEV("p", {
      children: ["\uC774\uC6A9\uC790\uAC00 \uC790\uB8CC, \uD14D\uC2A4\uD2B8, \uC18C\uD504\uD2B8\uC6E8\uC5B4, \uC74C\uC545, \uC74C\uD5A5\uC790\uB8CC, \uC0AC\uC9C4, \uADF8\uB798\uD53D, \uC774\uBBF8\uC9C0, \uBE44\uB514\uC624, \uBA54\uC2DC\uC9C0, \uAE30\uD0C0 \uC790\uB8CC \uB4F1(\"\uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\")\uC744 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0\uC11C \uC804\uC1A1, \uC5C5\uB85C\uB4DC, \uAC8C\uC2DC, \uC774\uBA54\uC77C \uC804\uC1A1 \uB610\uB294 \uB2EC\uB9AC \uC774\uC6A9\uAC00\uB2A5\uD558\uB3C4\uB85D \uD560 \uACBD\uC6B0, \uC774\uC6A9\uC790\uB294 \uADF8\uC640 \uAC19\uC740 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC5D0 \uB300\uD574 \uC804\uC801\uC73C\uB85C \uCC45\uC784\uC744 \uBD80\uB2F4\uD569\uB2C8\uB2E4. \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uB294 \uC704 \uC81C10\uC870\uC5D0 \uC815\uC758\uB41C \uC774\uC6A9\uC790\uAC00 \uC81C\uACF5\uD55C \uC815\uBCF4\uC5D0 \uD574\uB2F9\uD569\uB2C8\uB2E4. \uC774\uB294 \uD68C\uC0AC\uAC00 \uC544\uB2CC, \uBAA8\uB4E0 \uC774\uC6A9\uC790\uAC00 \uC0AC\uC774\uD2B8\uC5D0 \uAC8C\uC2DC\uD558\uB294 \uBAA8\uB4E0 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC5D0 \uB300\uD558\uC5EC \uC804\uC801\uC73C\uB85C \uCC45\uC784\uC744 \uC9C4\uB2E4\uB294 \uAC83\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4. \uC774\uC6A9\uC790\uB294 (a) \uBD88\uBC95\uC801\uC774\uAC70\uB098, \uD574\uB86D\uAC70\uB098, \uC704\uD611\uC801\uC774\uAC70\uB098, \uD3ED\uB825\uC801\uC774\uAC70\uB098, \uB610\uB294 \uD0C0\uC778\uC758 \uC2EC\uAE30\uB97C \uBD88\uD3B8\uD558\uAC8C \uD558\uAC70\uB098, \uBA85\uC608\uD6FC\uC190\uC801\uC774\uAC70\uB098, \uCC9C\uBC15\uD558\uAC70\uB098, \uC678\uC124\uC801\uC774\uAC70\uB098, \uBE44\uBC29\uC801\uC774\uAC70\uB098, \uD0C0\uC778\uC758 \uC0AC\uC0DD\uD65C\uC744 \uCE68\uD574\uD558\uAC70\uB098, \uD610\uC624\uC2A4\uB7FD\uAC70\uB098, \uC544\uB2C8\uBA74 \uC778\uC885\uC801\uC73C\uB85C \uB610\uB294 \uC724\uB9AC\uC801\uC73C\uB85C \uBC18\uAC10\uC744 \uBD88\uB7EC\uC77C\uC73C\uD0A4\uB294 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uB97C \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0\uC11C \uC804\uC1A1, \uC5C5\uB85C\uB4DC, \uAC8C\uC2DC, \uC774\uBA54\uC77C \uC804\uC1A1 \uB610\uB294 \uB2EC\uB9AC \uACF5\uAC1C\uD560 \uC218 \uC5C6\uACE0, \uB2E4\uB978 \uC0AC\uB78C\uB4E4\uC774 \uC774\uB7EC\uD55C \uCEE8\uD150\uCE20\uB97C \uBCF8 \uC0AC\uC774\uD2B8\uC5D0\uC11C \uC804\uC1A1, \uC5C5\uB85C\uB4DC, \uAC8C\uC2DC, \uC774\uBA54\uC77C \uC804\uC1A1 \uB610\uB294 \uB2EC\uB9AC \uACF5\uAC1C\uD558\uB3C4\uB85D \uB3D5\uAC70\uB098 \uC870\uC7A5\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. (b) \uB610\uD55C \uC774\uC6A9\uC790\uB294 \uBC95\uB960\uC774\uB098 \uACC4\uC57D \uB610\uB294 \uC2E0\uB8B0 \uAD00\uACC4\uC0C1 \uC804\uC220\uD55C \uC131\uACA9\uC758 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uB97C \uC774\uC6A9\uAC00\uB2A5\uD558\uAC8C \uD560 \uAD8C\uB9AC\uB97C \uAC00\uC9C0\uC9C0 \uC544\uB2C8\uD558\uBA70, (c) \uD68C\uC0AC\uC640 \uC774\uC6A9\uC790 \uC591\uC790 \uC911 \uC77C\uBC29\uC758 \uD2B9\uD5C8\uAD8C, \uC0C1\uD45C\uAD8C, \uC601\uC5C5\uBE44\uBC00, \uC800\uC791\uAD8C \uB610\uB294 \uAE30\uD0C0 \uC7AC\uC0B0\uAD8C\uC744 \uCE68\uD574\uD558\uB294 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uB97C \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uC5D0\uC11C \uC804\uC1A1, \uC5C5\uB85C\uB4DC, \uAC8C\uC2DC, \uC774\uBA54\uC77C \uC804\uC1A1 \uB610\uB294 \uB2EC\uB9AC \uC774\uC6A9\uAC00\uB2A5\uD558\uAC8C \uD560 \uC218 \uC5C6\uB2E4\uB294 \uC810\uC5D0 \uB300\uD558\uC5EC \uB3D9\uC758\uD569\uB2C8\uB2E4. \uB610\uD55C, \uC774\uC6A9\uC790\uB294 \uC5F0\uC1C4\uBA54\uC77C, \uB300\uB7C9\uBA54\uC77C \uB610\uB294 \uC5EC\uD0C0 \uD615\uD0DC\uC758 \u201C\uC2A4\uD338\u201D\uC744 \uD3EC\uD568\uD558\uC5EC \uC18C\uD504\uD2B8\uC6E8\uC5B4 \uBC14\uC774\uB7EC\uC2A4, \uC6D0\uCE58\uC54A\uB294 \uAD11\uACE0\uBB3C\uC774\uB098 \uC2B9\uC778\uBC1B\uC9C0 \uC54A\uC740 \uAD11\uACE0\uBB3C, \uAD6C\uB9E4\uC694\uCCAD\uC774\uB098 \uD64D\uBCF4\uC790\uB8CC \uB4F1\uC744 \uC804\uC1A1, \uC5C5\uB85C\uB4DC, \uAC8C\uC2DC, \uC774\uBA54\uC77C \uC804\uC1A1 \uB610\uB294 \uB2EC\uB9AC \uC774\uC6A9\uAC00\uB2A5\uD558\uAC8C \uD560 \uC218 \uC5C6\uB2E4\uB294 \uC810\uC5D0 \uB300\uD558\uC5EC \uB3D9\uC758\uD569\uB2C8\uB2E4. \uBFD0\uB9CC \uC544\uB2C8\uB77C, \uC774\uC6A9\uC790\uB294 (i) \uB2E4\uB978 \uC0AC\uB78C\uC744 \uD5C8\uC704\uB85C \uB300\uB9AC\uD558\uAC70\uB098, \uD2B9\uC815\uC778\uC774\uB098 \uD2B9\uC815 \uB2E8\uCCB4\uC640\uC758 \uAD00\uACC4\uB97C \uD5C8\uC704\uB85C \uB610\uB294 \uBD80\uC815\uD655\uD558\uAC8C \uC9C4\uC220\uD558\uC9C0 \uC544\uB2C8\uD558\uACE0, (ii) \uC81C3\uC790\uB97C \"\uC2A4\uD1A0\uD0B9\u201D\uD558\uAC70\uB098 \uB610\uB294 \uB2EC\uB9AC \uAD34\uB86D\uD788\uAC70\uB098, \uBAA8\uD568\uD558\uAC70\uB098, \uD574\uB97C \uB07C\uCE58\uC9C0 \uC544\uB2C8\uD558\uBA70, (iii) \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC758 \uCD9C\uCC98\uB97C \uAC00\uC7A5\uD558\uAE30 \uC704\uD558\uC5EC \uD5E4\uB354(header)\uB97C \uC704\uC870\uD558\uAC70\uB098 \uC2DD\uBCC4\uD45C\uC2DC \uB4F1\uC744 \uB2EC\uB9AC \uC870\uC791\uD558\uC9C0 \uC544\uB2C8\uD558\uACE0, (iv) \uACE0\uC758\uC131\uC758 \uC720\uBB34\uB97C \uBD88\uBB38\uD558\uACE0 \uAD00\uB828 \uC9C0\uC5ED, \uAD6D\uAC00\uC758 \uBC95\uB960 \uB610\uB294 \uAD6D\uC81C\uBC95\uC744 \uC704\uBC18\uD558\uC9C0 \uC544\uB2C8\uD558\uBA70, (v) \uB2E4\uB978 \uC774\uC6A9\uC790\uC5D0 \uB300\uD558\uC5EC \uAC1C\uC778 \uC2DD\uBCC4\uC774 \uAC00\uB2A5\uD55C \uB370\uC774\uD130\uB97C \uC218\uC9D1\uD558\uAC70\uB098 \uC800\uC7A5\uD558\uC9C0 \uC544\uB2C8\uD55C\uB2E4\uB294 \uC810\uC5D0 \uB300\uD558\uC5EC \uB3D9\uC758\uD569\uB2C8\uB2E4.", /*#__PURE__*/_jsxDEV("br", {}, void 0, false, {
        fileName: _jsxFileName,
        lineNumber: 132,
        columnNumber: 1
      }, this), "\uD68C\uC0AC\uB294 \uC774\uC6A9\uC790\uAC00 \uC0DD\uC131\uD558\uC5EC \uC0AC\uC774\uD2B8\uC5D0 \uC804\uC1A1 \uB610\uB294 \uAC8C\uC2DC\uD55C \uCEE8\uD150\uCE20\uB97C \uBCF4\uC99D\uD558\uAC70\uB098 \uC81C\uC5B4\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C \uD68C\uC0AC\uB294 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC758 \uC815\uD655\uC131, \uC9C4\uC2E4\uC131 \uB610\uB294 \uD488\uC9C8\uC744 \uBCF4\uC7A5\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uC774\uC6A9\uC790\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uB97C \uC0AC\uC6A9\uD568\uC73C\uB85C\uC368 \uC774\uC6A9\uC790\uC5D0\uAC8C \uBD88\uCF8C\uD558\uACE0 \uC74C\uB780\uD558\uBA70 \uBC18\uAC10\uC744 \uC77C\uC73C\uD0AC \uC218 \uC788\uB294 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC5D0 \uB178\uCD9C\uB420 \uC218 \uC788\uB2E4\uB294 \uC810\uC744 \uC591\uC9C0\uD569\uB2C8\uB2E4. \uC5B4\uB5A0\uD55C \uC0C1\uD669\uC5D0\uC11C\uB3C4, \uD68C\uC0AC\uB294 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC0C1 \uC624\uB958\uB098 \uB204\uB77D\uC744 \uD3EC\uD568\uD55C \uC77C\uCCB4\uC758 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC5D0 \uB300\uD574\uC11C, \uB610\uB294 \uBE0C\uB79C\uB4DC \uC6F9\uC0AC\uC774\uD2B8\uB97C \uD1B5\uD558\uC5EC \uC804\uC1A1, \uC5C5\uB85C\uB4DC, \uAC8C\uC2DC, \uC774\uBA54\uC77C \uC804\uC1A1 \uB610\uB294 \uB2EC\uB9AC \uACF5\uAC1C\uB41C \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uB97C \uC0AC\uC6A9\uD568\uC73C\uB85C\uC368 \uC774\uC6A9\uC790\uC5D0\uAC8C \uBC1C\uC0DD\uD55C \uBAA8\uB4E0 \uC131\uACA9\uC758 \uC190\uC2E4\uC774\uB098 \uC190\uD574\uC5D0 \uB300\uD574\uC11C \uC77C\uCCB4\uC758 \uCC45\uC784\uC744 \uC9C0\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.", /*#__PURE__*/_jsxDEV("br", {}, void 0, false, {
        fileName: _jsxFileName,
        lineNumber: 134,
        columnNumber: 1
      }, this), "\uC774\uC6A9\uC790\uB294 \uD68C\uC0AC\uAC00 \uD68C\uC0AC\uC758 \uC7AC\uB7C9\uC73C\uB85C \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC758 \uAC8C\uC2DC\uB97C \uAC70\uBD80\uD558\uAC70\uB098 \uB3D9 \uCEE8\uD150\uCE20\uB97C \uC81C\uAC70\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC(\uC758\uBB34\uB294 \uC544\uB2D8)\uB97C \uAC00\uC9D0\uC744 \uC778\uC815\uD569\uB2C8\uB2E4. \uC804\uC220\uC0AC\uD56D\uC774\uB098 \uBCF8 \uC57D\uAD00\uC758 \uC5EC\uD0C0 \uC870\uD56D\uC758 \uC77C\uBC18\uC131\uC744 \uC81C\uD55C\uD558\uC9C0 \uC54A\uACE0, \uD68C\uC0AC\uB294 \uBCF8 \uC57D\uAD00\uC744 \uC704\uBC18\uD558\uAC70\uB098 \uB2EC\uB9AC \uBC18\uAC10\uC744 \uC77C\uC73C\uD0A4\uB294 \uC774\uC6A9\uC790 \uCEE8\uD150\uCE20\uC5D0 \uB300\uD574\uC11C\uB294 \uC774\uB97C \uC81C\uAC70\uD560 \uC218 \uC788\uB294 \uAD8C\uB9AC\uB97C \uC9C0\uB2C8\uBA70, \uBCF8 \uC57D\uAD00\uC744 \uC704\uBC18\uD558\uAC70\uB098 \uD0C0\uC778\uC758 \uAD8C\uB9AC\uB97C \uCE68\uD574\uD558\uB294 \uC774\uC6A9\uC790\uC5D0 \uB300\uD574\uC11C\uB294 \uC11C\uBE44\uC2A4\uC758 \uC81C\uACF5\uC744 \uAC70\uBD80\uD558\uAC70\uB098 \uB610\uB294 \uC11C\uBE44\uC2A4\uC758 \uC81C\uACF5\uC744 \uAC70\uBD80\uD558\uACE0 \uC774\uC6A9\uC790\uC758 \uACC4\uC815\uC744 \uD574\uC9C0\uD560 \uAD8C\uB9AC\uB97C \uBCF4\uC720\uD569\uB2C8\uB2E4. \uD68C\uC0AC\uAC00 \uC774\uC6A9\uC790\uC758 \uACC4\uC815\uC744 \uD574\uC9C0\uD558\uB294 \uACBD\uC6B0 \uD68C\uC0AC\uB294 \uC774\uB97C \uC774\uC6A9\uC790\uC5D0\uAC8C \uD1B5\uC9C0\uD558\uACE0, \uC774\uC6A9\uC790\uC758 \uACC4\uC815\uC744 \uD574\uC9C0\uD558\uAE30 \uC804\uC5D0 \uCD5C\uC18C\uD55C 30\uC77C \uC774\uC0C1\uC758 \uAE30\uAC04\uC744 \uC815\uD558\uC5EC \uC774\uC6A9\uC790\uC5D0\uAC8C \uC18C\uBA85\uD560 \uAE30\uD68C\uB97C \uBD80\uC5EC\uD569\uB2C8\uB2E4."]
